import React, { useState, useEffect } from 'react';
import SearchBar from './SearchBar';
import Artists from './Artists';
import bands from './fake_band_names_mit.txt';
import logo from './logo.png'

// npm library to employ Dice's Coefficient: https://www.npmjs.com/package/string-similarity
import stringSimilarity from 'string-similarity';

// App architecture via SearchPage, Artists, and SearchBar Components
// inspired by an article from Praditiya Adhitana:
// https://medium.com/@pradityadhitama/simple-search-bar-component-functionality-in-react-6589fda3385d

const SearchPage = (props) => {
  const [input, setInput] = useState('');
  const [artistsDefault, setArtistsDefault] = useState();
  const [artists, setArtists] = useState();

  // fetch band names from local file
  const fetchData = async () => {
    return await fetch(bands)
    .then(r => r.text())
    .then(text => {
      const artists = text.split('\n')
      setArtists('');
      setArtistsDefault(artists);
    });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    const response = fetch('/api/query', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ query: input }),
    });
    const body = await response.text();
  };


  // update content in search box.
  const updateInput = async (input) => {
    setInput(input);
  }

  // POST function, update list of artists to display based on query
  function create(e) {
    e.preventDefault();
    const requestOptions = {
       method: 'POST',
       body: JSON.stringify({ query: e.target.value })
     };
     fetch(('/api/query/' + input), requestOptions)
       .then(response => console.log("hey"))
       .then(data => console.log("HEY"));

    const filteredArtists = searchArtists(input);
    setArtists(filteredArtists);
  }

  // modified from 'findBestMatch' function from string-similarity npm library (see above)
  function findBestMatches(mainString, targetStrings) {
  	const ratings = [];
  	let bestMatchIndex = 0;
  	for (let i = 0; i < targetStrings.length; i++) {
  		const currentTargetString = targetStrings[i];
  		var currentRating = stringSimilarity.compareTwoStrings(mainString.toLowerCase(), currentTargetString.toLowerCase())

      // Additional scoring for matching first characters of band name (more accurate results for short queries)
      if(mainString.toLowerCase() === currentTargetString.toLowerCase().substring(0, mainString.length)){
        currentRating += mainString.length / 5;
      }
  		ratings.push({target: currentTargetString, rating: currentRating})
  		if (currentRating > ratings[bestMatchIndex].rating) {
  			bestMatchIndex = i
  		}
  	}
  	const bestMatch = ratings[bestMatchIndex]
  	return { ratings: ratings, bestMatch: bestMatch, bestMatchIndex: bestMatchIndex };
  }

  // the main algorithm for searching, ranking relevant artist names.\

  function searchArtists(query){
    var rankedArtists = [];
    let artists = artistsDefault.map((x) => x);
    var similarity;
    for(var i = 0; i < 10; i++){
      similarity = findBestMatches(query, artists)
      rankedArtists[i] = similarity.bestMatch.target;
      artists.splice(similarity.bestMatchIndex, 1)
    }
    return rankedArtists;
  };

  useEffect( () => {fetchData()},[]);

  return (
    <>
      <a href="https://fan.booster.musicfox.io">
        <img src={ logo }/>
      </a>
      <h1 style={{fontSize: 25, fontFamily: 'system-ui,-apple-system,Segoe UI,Roboto,Helvetica,Arial,sans-serif,Apple Color Emoji,Segoe UI Emoji'}}>Our Bands</h1>
      <SearchBar
       input={input}
       onChange={updateInput}
      />
    <button
      type="button"
      onClick={handleSubmit}
      style={{height: 31, backgroundColor: 'black', borderColor: 'black', borderRadius: 4, outlineColor: '#D1D1D1', fontWeight: 'bold', fontFamily: 'system-ui,-apple-system,Segoe UI,Roboto,Helvetica,Arial,sans-serif,Apple Color Emoji,Segoe UI Emoji', color: 'white'}}
      >
        SEARCH
      </button>
      {artists != ''? <Artists artists={artists}/> : null}
    </>
   );
}

export default SearchPage
